mapper.select:
SELECT count(1)
FROM T_SYS_org_menu om inner join t_sys_menu m on m.id = om.menu_id
WHERE m.flag = 1 AND m.PARENT_ID = #{parentId} and om.org_id = #{orgId}

SELECT * FROM T_SYS_MENU

    PARENT_ID = #{parentId}

        and flag=#{flag}


order by sort_num asc

SELECT MAX(sort_num) AS sort_num
FROM t_sys_menu
WHERE parent_id = #{parentId};

select id,name,url,parent_id,icon,flag,sort_num,GROUP_CONCAT(DISTINCT operation_authority) operation_authority from t_sys_menu m where 1 = 1
and exists ( select 1 from t_sys_role_menu rm inner join t_sys_user_role ur on ur.role_id = rm.role_id where rm.menu_id = m.id and ur.user_id = #{userId} )
and m.parent_id = #{parentId}
and m.flag = #{flag}
group by id,name,url,parent_id,icon,flag,sort_num order by flag, sort_num

mapper.update:
update t_sys_org_menu
set menu_name = #{name}, sort_num = #{sortNum}
where menu_id = #{id} and org_id = #{orgId}

update t_sys_Role_menu
set menu_name = #{name}, sort_num = #{sortNum}
where menu_id = #{id} and role_id = #{roleId}

mapper.select:
select flag
from t_sys_menu
where id = #{id}
