mapper.select:
select id,number,type,issue_date issueDate,valid_date validDate,issue_organs issueOrgans,user_id userId,status
from t_basic_qualification
where id = #{id}

mapper.update:
UPDATE T_BASIC_QUALIFICATION
number=#{number}, type=#{type}, issue_date=#{issueDate}, valid_date=#{validDate}, issue_organs=#{issueOrgans}, update_time=#{updateTime},
WHERE ID = #{id}
